class HighlightsWrapper extends Component {

    constructor(props){
        super(props);
        this.scroll = this.scroll.bind(this);
        // here is the popup state
        this.state = { showPopup: false };
        this.togglePopup = this.togglePopup.bind(this);
    }
        // function to toggle popup
    togglePopup() {
        this.setState({
            showPopup: !this.state.showPopup,
        });
    }

    scroll(direction){
        let far = $( '.image-container' ).width()/1*direction;
        let pos = $('.image-container').scrollLeft() + far;
        $('.image-container').animate( { scrollLeft: pos }, 1000)
    }

    render () {
        return (
            <div className='main'>
                <div className='wrapper'>
                    <a className="prev" onClick={this.scroll.bind(null,-1)}>&#10094;</a>
                <div className="image-container">
                    {highlightsArr.map((el) =>
                     <Highlight showPopupState ={this.state.showPopup} togglePopup={this.togglePopup} name={el.name} image={el.image} info={el.text}/>

                     )}

                </div>
                    <a className="next" onClick={this.scroll.bind(null,1)}>&#10095;</a>
                </div>
            </div>
        )
    }
}

export default HighlightsWrapper;
